import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import MenuItem from '@mui/material/MenuItem';

export default function FormDialog({precio, onClose}) {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  const [tipo, setTipo] = useState(''); // Estado para rastrear el valor del tipo
  const [frecuencia, setFrecuencia] = useState(''); // Estado para rastrear el valor de la frecuencia
  const [descripcion, setDescripcion] = useState('');


  const handleTipoChange = (event) => {
    setTipo(event.target.value); // Actualiza el valor del tipo cuando cambia
  };

  const handleFrecuenciaChange = (event) => {
    setFrecuencia(event.target.value); // Actualiza el valor de la frecuencia cuando cambia
  };

  const handleDescripcionChange = (event) => {
    setDescripcion(event.target.value);
  };



  return (
    <div>
      <Button fullWidth disableRipple  onClick={handleClickOpen} sx={{
        fontSize: "large",
        backgroundColor: "#005B96",
        borderRadius: "10px",
        color: "white",
        maxWidth: "1300px"
        
    }}>
        <h1>${precio}</h1>
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here. We
            will send updates occasionally.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Nombre"
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="surname"
            label="Apellido"
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="email"
            label="email"
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="cclases"
            label="Cantidad de claces"
            type="number"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Nombre"
            type="text"
            fullWidth
            variant="standard"
          />

<TextField
      margin="dense"
      id="type"
      label="Tipo"
      select
      fullWidth
      value={tipo}
      onChange={handleTipoChange}
      variant="standard"
      InputProps={{
        
      }}
    >
      <MenuItem value="Individual">Individual</MenuItem>
      <MenuItem value="Grupal">Grupal</MenuItem>
    </TextField>

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Subscribe</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
