import React from 'react';
import './menu.css';
import Slider from '@mui/material/Slider';
import Typography from '@mui/material/Typography';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';

const Sidebar = () => {
  const marks = [
    { value: 1, label: '1' },
    { value: 2, label: '2' },
    { value: 3, label: '3' },
    { value: 4, label: '4' },
    { value: 5, label: '5' },
  ];

  return (
    <div className='sidebar'>
      <header>
        <h3>Filtros</h3>
      </header>

      <div className="filter-container">
        <div className="filter">
          <InputLabel className="custom-label">Tipo de clase</InputLabel>
          <Select className="custom-select" label="Tipo de clase">
            <MenuItem value="individual">Individual</MenuItem>
            <MenuItem value="grupal">Grupal</MenuItem>
          </Select>
        </div>

        <div className="filter">
          <InputLabel className="custom-label">Frecuencia</InputLabel>
          <Select className="custom-select" label="Frecuencia">
            <MenuItem value="unica">Única</MenuItem>
            <MenuItem value="semanal">Semanal</MenuItem>
            <MenuItem value="mensual">Mensual</MenuItem>
          </Select>
        </div>

        <div className="filter">
          <Typography id="calificacion-slider" gutterBottom>
            <strong style={{ textAlign: 'center', display: 'block' }}>Calificación</strong>
          </Typography>
          <Slider
            defaultValue={3}
            step={1}
            min={1}
            max={5}
            valueLabelDisplay="auto"
            aria-labelledby="calificacion-slider"
            marks={marks}
            labelPlacement="top"
          />
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
