import './menu.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Link } from 'react-router-dom';

const NavigationBar = () => {
  return (
    // Barra de navegación utilizando React Bootstrap
    <Navbar expand="lg" bg="dark" data-bs-theme="dark" className="bg-body-tertiary">
      <Container fluid>
        {/* Marca del sitio (con enlace a Home) */}
        <Navbar.Brand as={Link} to="/">ClaseYA</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          {/* Menú de navegación centrado */}
          <Nav className="mx-auto my-2 my-lg-0" style={{ maxHeight: '100px' }} navbarScroll>
            {/* Enlaces de inicio, perfil y historial */}
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/perfil">Perfil</Nav.Link>
            <Nav.Link as={Link} to="/historial">Historial</Nav.Link>
          </Nav>
          {/* Formulario de búsqueda y avatar de inicio de sesión */}
          <div className="d-flex">
            {/* Formulario de búsqueda */}
            <Form className="me-2">
              <Form.Control
                type="search"
                placeholder="Buscar por categoría"
                aria-label="Buscar"
              />
            </Form>
            {/* Avatar de inicio de sesión con Dropdown */}
            <div className="dropdown">
              <Button variant="light" id="dropdownMenuButton" data-bs-toggle="dropdown">
                Avatar {/* Puedes reemplazarlo con la imagen del avatar */}
              </Button>
              <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                <li>
                  <Link className="dropdown-item" to="/iniciarsesion">
                    Iniciar sesión
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-item" to="/registrarse">
                    Registrarse
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
