import './menu.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Link } from 'react-router-dom';
import Dropdown from 'react-bootstrap/Dropdown';

const NavigationBar = () => {
  return (
    // Barra de navegación utilizando React Bootstrap
    <Navbar expand="lg" bg="dark" data-bs-theme="dark" className="bg-body-tertiary">
      <Container fluid>
        {/* Marca del sitio (con enlace a Home) */}
        <Navbar.Brand as={Link} to="/">ClaseYA</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          {/* Menú de navegación centrado */}
          <Nav className="mx-auto my-2 my-lg-0" style={{ maxHeight: '100px' }} navbarScroll>
            {/* Enlaces de inicio, perfil y historial */}
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/perfil">Perfil</Nav.Link>
            <Nav.Link as={Link} to="/historial">Historial</Nav.Link>
          </Nav>
          {/* Formulario de búsqueda y botón de iniciar sesión con Dropdown */}
          <div className="d-flex">
            {/* Botón de inicio de sesión */}
            <Link to="/iniciarsesion" className="btn btn-light me-2">
              Iniciar sesión
            </Link>
            {/* Formulario de búsqueda */}
            <Form className="me-2">
              <Form.Control
                type="search"
                placeholder="Buscar por categoría"
                aria-label="Buscar"
              />
            </Form>
            {/* Botón de buscar con Dropdown */}
            <Dropdown>
              <Button variant="light" id="dropdownMenuButton" data-bs-toggle="dropdown">
                Buscar
              </Button>
              <Dropdown.Menu aria-labelledby="dropdownMenuButton">
                <Dropdown.Item as={Link} to="/registrarse">Registrarse</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
