import React, { useState } from 'react';
import Avatar from '@mui/material/Avatar';
import Stack from '@mui/material/Stack';
import { deepOrange } from '@mui/material/colors';
import Slider from '@mui/material/Slider';
import Typography from '@mui/material/Typography';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import 'bootstrap/dist/css/bootstrap.min.css';
import './menu.css';
import NavigationBar from './NavBar';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import HistorialSite from '../Historial/Historial-site';
import Profilesite from '../Perfil/perfil-site';
import userLogin from '../Login/LoginForm';

const getAvatarColor = (name) => {
  const firstLetter = name.charAt(0).toUpperCase();
  // Aquí puedes agregar lógica para obtener un color de Avatar basado en el nombre si lo deseas.
};

const Postlist = () => {
  const [posts, setPosteos] = useState([
    {
      author: 'Mario',
      titulo: 'Abogado',
      experiencia: 'lorem ipsum...',
      categoria: 'Piano',
      precio: '50',
      calificacion: '4',
      id: 1,
    },
    {
      author: 'Mariana',
      titulo: 'Matematicas',
      experiencia:
        'Lorem ipsum dolor sit amet consectetur adipiscing elit aliquam, conubia suspendisse mauris sed felis augue ac torquent molestie, suscipit a velit pellentesque aptent habitasse neque. Fermentum class aliquet augue fames habitasse magna varius hac, rhoncus et dapibus pellentesque nisl senectus bibendum natoque, tempor suspendisse sem praesent dignissim vehicula litora. Purus duis magna tellus cum fringilla accumsan sed, posuere tempor lectus per aenean diam litora ac, dignissim hac sociosqu felis ornare primis.',
      categoria: 'Masticar',
      precio: '70',
      calificacion: '5',
      id: 2,
    },
    {
      author: 'Kevin',
      titulo: 'Botanica',
      experiencia: 'lorem ipsum...',
      categoria: 'Manejo',
      precio: '100',
      calificacion: '3',
      id: 3,
    },
    {
      author: 'Patricia',
      titulo: 'Fisica',
      experiencia: 'lorem ipsum...',
      categoria: 'Matematicas',
      precio: '60',
      calificacion: '2',
      id: 4,
    },
    {
      author: 'Lucas',
      titulo: 'Artista',
      experiencia: 'lorem ipsum...',
      categoria: 'Circo',
      precio: '50',
      calificacion: '4',
      id: 5,
    },
    {
      author: 'Raquel',
      titulo: 'Defensas Personales',
      experiencia: 'lorem ipsum...',
      categoria: 'Natación',
      precio: '40',
      calificacion: '5',
      id: 6,
    },
  ]);

  return (
    <div className="post-container">
      <div className="post-grid">
        {posts.map((post) => (
          <div className="post-preview" key={post.id}>
            <div className="head-post">
              <div className="class-category">
                <h2>{post.categoria}</h2>
              </div>
            </div>
            <div className="avatar">
              <Stack direction="row" spacing={2}>
                <Avatar sx={{ bgcolor: deepOrange[500] }}>
                  {post.author.charAt(0).toUpperCase()}
                </Avatar>
              </Stack>
            </div>
            <ul>
              <li>Nombre: {post.author}</li>
              <li>Titulo: {post.titulo}</li>
              <li>Experiencia: {post.experiencia}</li>
              <li>Calificacion: {post.calificacion}</li>
            </ul>
            <div className="precio-clase">
              <h1>{post.precio}/h</h1>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const Sidebar = () => {
  const marks = [
    { value: 1, label: '1' },
    { value: 2, label: '2' },
    { value: 3, label: '3' },
    { value: 4, label: '4' },
    { value: 5, label: '5' },
  ];

  return (
    <div className="sidebar">
      <header>
        <h3>Filtros</h3>
      </header>

      <div className="filter-container">
        <div className="filter">
          <InputLabel className="custom-label">Tipo de clase</InputLabel>
          <Select className="custom-select" label="Tipo de clase">
            <MenuItem value="individual">Individual</MenuItem>
            <MenuItem value="grupal">Grupal</MenuItem>
          </Select>
        </div>

        <div className="filter">
          <InputLabel className="custom-label">Frecuencia</InputLabel>
          <Select className="custom-select" label="Frecuencia">
            <MenuItem value="unica">Única</MenuItem>
            <MenuItem value="semanal">Semanal</MenuItem>
            <MenuItem value="mensual">Mensual</MenuItem>
          </Select>
        </div>

        <div className="filter">
          <Typography id="calificacion-slider" gutterBottom>
            <strong style={{ textAlign: 'center', display: 'block' }}>
              Calificación
            </strong>
          </Typography>
          <Slider
            defaultValue={3}
            step={1}
            min={1}
            max={5}
            valueLabelDisplay="auto"
            aria-labelledby="calificacion-slider"
            marks={marks}
            labelPlacement="top"
          />
        </div>
      </div>
    </div>
  );
};

const HomeMenu = () => {
  return (
    <Router>
      <NavigationBar />
      <div className="mainMenu">
        <Switch>
          <Route path="/historial" component={HistorialSite} />
          <Route path="/perfil" component={Profilesite} />
          <Route path="/iniciarsesion" component={userLogin} />
          <Route path="/">
            <div className="aside-container">
              <Sidebar />
            </div>
            <div className="feed-container">
              <div className="selected-filters">{/* Espacio para filtros seleccionados */}</div>
            </div>
            <div className="feed-post">
              <Postlist />
            </div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default HomeMenu;
